// Questa funzione controlla l'eta e in base a se è minore o maggiore di 18 setta la variabile message
// Non ci sono errori di sintassi
//Messagge deve essere dichiarato come let per essere utilizzato e modificato all'interno del costrutto if
//Soluzioni
function checkAge() {
    const myAge = 32;
    let message = '';

    if (myAge < 18) {
        message = `Sei troppo giovane! Hai ${myAge} anni!`;
    } else {
        message = 'Hai più di 18 anni!';
    }

    return message;
}
console.log(checkAge());



