//Questa funzione vuole fare una somma tra un numero inserito dall'utente e 12
//Non ci sono errori di sintassi
//Bisogna trasformare con parseInt() usernumber perché dal prompt ritorna una stringa con cui farebbe concatenazione
//Soluzione
function addNumbers() {
    const userNumber = parseInt(prompt('Inserisci un numero'));
    const total = userNumber + 12;

    console.log(`Il risultato finale è ${total}`);
}

addNumbers();



