//Questa funzione controlla se l'email dell'utente in ingresso
//Non ci sono errori di sintassi
//Vogliamo che grantAccess sia una variabile di tipo booleano per la nostra condizione, perciò è scorretto inserire delle stringhe
//Soluzione
function checkAccess() {
    const addresses = ['mymail@mail.com', 'yourmail@mail.com', 'hermail@mail.com', 'hismail@mail.com'];
    const userEmail = prompt('Inserisci il tuo indirizzo email');

    let grantAccess = false;

    if (addresses.includes(userEmail)) {
        grantAccess = true;
    }

    if (grantAccess === true) {
        console.log('Accesso consentito!');
    } else {
        console.log('Accesso negato!');
    }
}

checkAccess();


